import React, { useContext } from "react";
import logo from "../assets/img/logo.png";
import user from "../assets/img/user.png";
import AuthContext from "../context/AuthContext";

const Menu = () => {
  const { userData } = useContext(AuthContext);

  const stylePointer = {
    cursor: "pointer",
    color: "white",
  };

  const handleRutas = (e) => {
    console.log(e.value);
  };

  return (
    <div>
      <aside className="main-sidebar sidebar-dark-primary elevation-4">
        {/* Brand Logo */}
        <a href="." className="brand-link">
          <img
            src={logo}
            alt="AdminLTE Logo"
            className="brand-image img-circle elevation-3"
            style={{ opacity: ".8", backgroundColor: "white" }}
          />
          <span className="brand-text font-weight-light">Taxifo</span>
        </a>
        {/* Sidebar */}
        <div className="sidebar">
          {/* Sidebar user panel (optional) */}
          <div className="user-panel mt-3 pb-3 mb-3 d-flex">
            <div className="image">
              <img
                src={user}
                alt={"User Imagen"}
                className="img-circle elevation-2"
                style={{ backgroundColor: "white" }}
              />
            </div>
            <div className="info">
              <a href="." className="d-block">
                {userData.nombre}
              </a>
            </div>
          </div>
          {/* Sidebar Menu */}
          <nav className="mt-2">
            <ul
              className="nav nav-pills nav-sidebar flex-column"
              data-widget="treeview"
              role="menu"
              data-accordion="false"
            >
              {/* Add icons to the links using the .nav-icon class
         with font-awesome or any other icon font library */}
              <li className="nav-header">Servicios Generales</li>
              <li className="nav-item">
                <p
                  className="nav-link "
                  style={stylePointer}
                  value=""
                  onClick={handleRutas}
                >
                  <i className="fa-solid fa-circle-info"></i>
                  <span> Información</span>
                </p>
              </li>
              <li className="nav-header">Servicios Agente</li>
              <li className="nav-item">
                <p
                  className="nav-link"
                  style={stylePointer}
                  onClick={handleRutas}
                >
                  <i className="fa-solid fa-magnifying-glass"></i>
                  <span> Consultar Dirección</span>
                </p>
              </li>
              <li className="nav-header">Servicios Secretaria</li>
              <li className="nav-item">
                <p
                  className="nav-link"
                  style={stylePointer}
                  onClick={handleRutas}
                >
                  <i className="fa-solid fa-people-roof"></i>
                  <span> Gestionar Cliente</span>
                </p>
              </li>
              <li className="nav-item">
                <p
                  className="nav-link"
                  style={stylePointer}
                  onClick={handleRutas}
                >
                  <i className="fa-solid fa-people-roof"></i>
                  <span> Gestionar Barrios</span>
                </p>
              </li>
            </ul>
          </nav>
          {/* /.sidebar-menu */}
        </div>
        {/* /.sidebar */}
      </aside>
    </div>
  );
};

export default Menu;
